css:
.button {
    color: #1a202c;
    display: flex;
    align-items: center;
    justify-content: space-between;
    border: 1px solid #2d3748;
    border-radius: 0.25rem;
    padding: 0.25rem 1.5rem;
    overflow: hidden;
  }

  .button svg {
    width: 1rem;
    height: 1rem;
    stroke-width: 2px;
    stroke: black;
    cursor: pointer;
    transform: translateX(-10rem);
    transition: all 0.3s ease-in-out;
  }

  .button svg:hover {
    stroke: #2c5282;
  }

  .button:hover svg {
    transform: translateX(-0.5rem);
  }

  .button span {
    transform: translateX(-0.5rem);
    transition: all 0.3s ease-in-out;
  }

  .button:hover span {
    transform: translateX(0.5rem);
  }
